import React, { useState } from "react";
import axios from "axios";
import {
  MainForm,
  FormContainer,
  FormLabel,
  FormInput,
  FlexItem,
  Button,
  TextArea,
  Error,
  Message,
  FormText,
  Social,
} from "../styles/Form";

export default function ContactForm() {
  const [status, setStatus] = useState({
    submitted: false,
    submitting: false,
    info: { error: false, msg: null },
  });

  const [inputs, setInputs] = useState({
    Name: "",
    Email: "",
    Linkedin: "",
  });

  const handleServerResponse = (ok, msg) => {
    if (ok) {
      setStatus({
        submitted: true,
        submitting: false,
        info: { error: false, msg: msg },
      });

      setInputs({
        Name: "",
        Email: "",
        Linkedin: "",
      });
    } else {
      setStatus({
        info: { error: true, msg: msg },
      });
    }
  };

  const handleOnChange = (e) => {
    e.persist();

    setInputs((prev) => ({
      ...prev,
      [e.target.id]: e.target.value,
    }));

    setStatus({
      submitted: false,
      submitting: false,
      info: { error: false, msg: null },
    });
  };

  const handleOnSubmit = (e) => {
    e.preventDefault();
    setStatus((prevStatus) => ({ ...prevStatus, submitting: true }));
    axios({
      method: "POST",
      url: "https://formspree.io/f/xpzoyzwj",
      data: inputs,
    })
      .then((response) => {
        handleServerResponse(true, "Хвала Вам, Ваша порука је послата.");
      })
      .catch((error) => {
        handleServerResponse(false, error.response.data.error);
      });
  };

  return (
    <>
      <div className="container-form">
        <FormText>
          Заказивање термина и више информација о часовима, можете добити преко
          телефона:
          <br></br>
          <br></br>
          +381 60 4560 068
          <br></br>
          <br></br>
          Или путем форме.
        </FormText>
      </div>

      <div className="container-form">
        <MainForm>
          <FormContainer onSubmit={handleOnSubmit}>
            <FlexItem>
              <FormLabel htmlFor="name">Ваше име:</FormLabel>
              <FormInput
                id="Name"
                type="Name"
                name="_replyto"
                onChange={handleOnChange}
                required
                value={inputs.Name}
              />
            </FlexItem>
            <FlexItem>
              <FormLabel htmlFor="Email">Ваша е-мејл адреса:</FormLabel>
              <FormInput
                id="Email"
                type="Email"
                name="_replyto"
                onChange={handleOnChange}
                required
                value={inputs.Email}
              />
            </FlexItem>

            <FlexItem>
              <FormLabel htmlFor="Email">Ваш број телефона:</FormLabel>
              <FormInput
                id="Email"
                type="Email"
                name="_replyto"
                onChange={handleOnChange}
                required
                value={inputs.Email}
              />
            </FlexItem>
            <FlexItem>
              <FormLabel htmlFor="message">Ваша порука или питање:</FormLabel>
              <TextArea
                id="message"
                name="message"
                onChange={handleOnChange}
                required
                value={inputs.message}
              />
            </FlexItem>

            <Button type="submit" disabled={status.submitting}>
              {!status.submitting
                ? !status.submitted
                  ? "Пошаљи"
                  : "Послато"
                : "Шаље се..."}
            </Button>
          </FormContainer>
          {status.info.error && <Error>Error: {status.info.msg}</Error>}
          {!status.info.error && status.info.msg && (
            <Message>{status.info.msg}</Message>
          )}
          <Social>Посетите Фејсбук и Јутјуб профил Мелодрома:</Social>
          <FlexSocial>
            <Link>
              <svg
                width="30px"
                height="30px"
                fill="white"
                version="1.1"
                id="Layer_1"
                xmlns="http://www.w3.org/2000/svg"
                x="0px"
                y="0px"
                viewBox="0 0 512 512"
                style="enable-background:new 0 0 512 512;"
              >
                <g transform="translate(1 1)">
                  <g>
                    <path
                      d="M468.333-1H41.667C17.773-1-1,17.773-1,41.667v426.667C-1,492.227,17.773,511,41.667,511h196.267h102.4h128
			C492.227,511,511,492.227,511,468.333V41.667C511,17.773,492.227-1,468.333-1z M255,493.933V323.267h-68.267V255H255v-59.733
			c0-48.64,37.547-89.6,85.333-93.867h76.8v68.267h-68.267c-14.507,0-25.6,11.093-25.6,25.6V255H408.6v68.267h-85.333v170.667H255z
			 M493.933,468.333c0,14.507-11.093,25.6-25.6,25.6h-128v-153.6h85.333v-102.4h-85.333v-42.667c0-5.12,3.413-8.533,8.533-8.533
			H434.2v-102.4h-94.72c-57.173,5.12-101.547,53.76-101.547,110.933v42.667h-68.267v102.4h68.267v153.6H41.667
			c-14.507,0-25.6-11.093-25.6-25.6V41.667c0-14.507,11.093-25.6,25.6-25.6h426.667c14.507,0,25.6,11.093,25.6,25.6V468.333z"
                    />
                  </g>
                </g>
                <g></g>
                <g></g>
                <g></g>
                <g></g>
                <g></g>
                <g></g>
                <g></g>
                <g></g>
                <g></g>
                <g></g>
                <g></g>
                <g></g>
                <g></g>
                <g></g>
                <g></g>
              </svg>
            </Link>
          </FlexSocial>
        </MainForm>
      </div>
    </>
  );
}
