import styled from "styled-components";

export const Wrap = styled.div`
  background: #faf7f7;
  height: 500px;
`;

export const Text = styled.div`
  padding-top: 150px;
  font-size: 23px;
  text-align: center;
  line-height: 1.5;
`;

export const CenteredText = styled.div`
  width: 700px;
  max-width: 100%;
  margin: 0px auto;
`;

export const SubText = styled.div`
  font-size: 23px;
  font-size: 21;
  text-align: center;
  line-height: 1.5;
  padding-top: 30px;
`;
