import styled from "styled-components";

export const Wrap = styled.div`
  position: relative;
  overflow: hidden;
`;

export const Text = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
 text-align: center;
  transform: translate(-50%, -50%);
  font-size: 21px;
 line-height: 1.7;
  color: black;
`;
