import React from "react";
import Slider from "react-slick";
import { Wrap } from "../styles/SliderText";

function SampleNextArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      onClick={onClick}
      style={{ display: "none" }}
    ></div>
  );
}

function SamplePrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      onClick={onClick}
      style={{ display: "none" }}
    ></div>
  );
}

export default function SimpleSlider() {
  var settings = {
    fade: true,
    autoplay: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <SampleNextArrow />,
    prevArrow: <SamplePrevArrow />,
  };
  return (
    <Wrap>
      <Slider {...settings}>
        <div>
          Kроз учење музике дете стиче неопходне предиспозиције да израсте у
          емоционално јаку и стабилну личност.
        </div>
        <div>
          Музика ангажује мозак на свим нивоима. Клавир помаже у развоју
          координације, концентрације и памћења.
        </div>
        <div>
          Укључивањем деце у музичке активности, а посебно свирање клавираа,
          побољшавају се вербалне способности.
        </div>
        <div>
          Свирање код детета гради истрајности и самопоуздање. Клавир помаже
          деци да израсту у зреле и самопоуздане људе.
        </div>
      </Slider>
    </Wrap>
  );
}
