import React, { useState, useRef } from "react";
import styled from "styled-components";
import { useOnClickOutside } from "../hooks";
import ScrollIntoView from "react-scroll-into-view";
import { HeaderItem } from "../styles/Banner";

const StyledMenu = styled.div`

  background: white;
  border-left: 1px solid #eaeaea;
  border-bottom: 1px solid #eaeaea;
  transform: ${({ open }) => (open ? "translateX(0%)" : "translateX(100%)")};
  height: 300px;
  text-align: left;
  padding: 80px 20px;
  position: fixed;
  z-index: 500;
  top: 0;
  right: 0;
  transition: transform 0.3s ease-in-out;
`;
const MenuItem = styled.div`
  color: black;

  margin-top: 30px;
  :hover {
    cursor: pointer;
  }
`;

const MenuComponent = ({ open, setOpen }) => {
  return (
    <StyledMenu open={open}>
      <MenuItem onClick={() => setOpen(!open)}>
        <ScrollIntoView selector="#about" smooth={true}>
          <HeaderItem>О радионици</HeaderItem>
        </ScrollIntoView>
      </MenuItem>

      <MenuItem onClick={() => setOpen(!open)}>
        <ScrollIntoView selector="#program" smooth={true}>
          <HeaderItem>Програм и локација</HeaderItem>
        </ScrollIntoView>
      </MenuItem>

      <MenuItem onClick={() => setOpen(!open)}>
        <ScrollIntoView selector="#contact" smooth={true}>
          <HeaderItem>Контакт</HeaderItem>
        </ScrollIntoView>
      </MenuItem>
    </StyledMenu>
  );
};

const StyledBurger = styled.button`
  position: fixed;
  z-index: 700;
  top: 20px;
  right: 2rem;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  width: 2rem;
  height: 2rem;
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 0;
  z-index: 10;

  &:focus {
    outline: none;
  }

  div {
    width: 2rem;
    height: 0.25rem;
    background: ${({ open }) => (open ? "#0D0C1D" : "#0D0C1D")};
    border-radius: 10px;
    transition: all 0.3s linear;
    position: relative;
    transform-origin: 1px;

    :first-child {
      transform: ${({ open }) => (open ? "rotate(45deg)" : "rotate(0)")};
    }

    :nth-child(2) {
      opacity: ${({ open }) => (open ? "0" : "1")};
      transform: ${({ open }) => (open ? "translateX(20px)" : "translateX(0)")};
    }

    :nth-child(3) {
      transform: ${({ open }) => (open ? "rotate(-45deg)" : "rotate(0)")};
    }
  }
`;

const Burger = ({ open, setOpen }) => {
  return (
    <StyledBurger open={open} onClick={() => setOpen(!open)}>
      <div />
      <div />
      <div />
    </StyledBurger>
  );
};

function Menu() {
  const [open, setOpen] = useState(false);
  const node = useRef();

  useOnClickOutside(node, () => setOpen(false));

  useEffect(() => {
    const onScroll = e => {
      setScrollTop(e.target.documentElement.scrollTop);
      setScrolling(e.target.documentElement.scrollTop > scrollTop);
    };
    window.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  }, [scrollTop]);

  return (
    <div>
      <div ref={node}>
        <Burger open={open} setOpen={setOpen} />
        <MenuComponent open={open} setOpen={setOpen} />
      </div>
    </div>
  );
}

export default Menu;
