import styled from "styled-components";

export const BannerWrap = styled.div`
  display: flex;
`;

export const BannerLeft = styled.div`
  width: 50%;
  z-index: 1;
  position: relative;
  padding: 60px 80px 80px;

`;

export const BannerRight = styled.div`
  width: 50%;
  z-index: 1;
  position: relative;
  padding: 60px 80px 80px;
  display: flex;
  flex-direction: column;
  background: #677D8C;
`;

export const ReasonTitle = styled.div`
 font-size: 16px;
 text-transform: uppercase;
 text-align: center;
 line-height: 1.5;
 font-family: "Gotham"
`;

export const MainText = styled.div`
 font-size: 18px;
 text-align: center;
 line-height: 1.5;
 margin-top: 30px;
 color: white;
`;

export const TerminText = styled.div`
 font-size: 18px;
 text-align: center;
 line-height: 1.5;
 margin-top: 30px;
 color: white;
`;
