import styled from "styled-components";

export const Wrap = styled.div`
position: relative;
overflow: hidden;
`;

export const Text = styled.div`
  position: absolute;
  top: 50%;
  left: 25%;
  width: 40%;
  transform: translate(-50%, -50%);
  font-size: 21px;
  padding: 20px;
  color: black;
  background-color: white;
`;
