import styled from "styled-components";

export const Wrap = styled.div`
  background: #eff0ec;
  height: 500px;
`;

export const Text = styled.div`
  padding-top: 60px;
  font-size: 18px;
  text-align: center;
  line-height: 1.8;
  display: flex;
`;

export const CenteredText = styled.div`
  width: 640px;
  max-width: 100%;
  margin: 0px auto;
`;

export const SVG = styled.svg`
  width: 30%;
  height: 30px;
`;

export const FlexItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: end;
  margin-top: 30px;
  font-size: 21px;

  @media (max-width: 1100px) {
    padding: 30px;
  }
`;

export const FlexText = styled.div`
  margin-left: 30px;
`;

export const FlexSVG = styled.div`
  margin-top: 10px;
`;
