import styled from "styled-components";

export const BannerWrap = styled.div`
  display: flex;
  height: 100vh;
`;

export const BannerLeft = styled.div`
  width: 50%;
  z-index: 1;
  position: relative;
  padding: 60px 80px 80px;
`;

export const BannerRight = styled.div`
  width: 50%;
  background: #d7e3e5;
  z-index: 1;
  position: relative;
  padding: 60px 80px 80px;
  display: flex;
  flex-direction: column;
`;
